import logo from './logo.svg';
import './App.css';
import Student_table from './components/Student_table';
import EditStudentForm from './components/EditStudentForm';
import AddStudentForm from './components/AddStudentForm';
import StudentProfile from './components/StudentProfile';
import { Moon, Sun } from 'lucide-react';

function App() {
  const [darkMode, setDarkMode] = useState(false);
  const handledarkmode = () => {
    setDarkMode(!darkMode);
    if (!darkMode) {
      document.documentElement.classList.add("dark");
    } else {
      document.documentElement.classList.remove("dark");
    }
  };
  // document.documentElement.classList.add("dark")
  return (
    <div className="App">
       <button
            onClick={() => {handledarkmode()}}
            className="self-start sm:self-auto p-3 rounded-xl bg-white dark:bg-gray-800 border border-gray-200 dark:border-gray-700 text-gray-600 dark:text-gray-400 hover:bg-gray-50 dark:hover:bg-gray-700 transition-colors shadow-sm"
          >
            {darkMode ? <Sun className="h-5 w-5" /> : <Moon className="h-5 w-5" />}
          </button>
      <Student_table/>
      {/* <EditStudentForm/> */}
      {/* <AddStudentForm/> */}
      <StudentProfile/>
    </div>
  );
}

export default App;
