import React, { useState } from 'react';

export default function AddStudentForm({setOpenHandleForm}) {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    handle: '',
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    // onSave({
    //   ...formData,
    //   id: Date.now(),
    //   currentRating: 0,
    //   maxRating: 0,
    // });
  };

  const handlecancel = () =>{
    setOpenHandleForm(false)
  }

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center backdrop-blur-sm bg-black/30">
      <form
        onSubmit={handleSubmit}
        className="bg-white w-11/12 sm:w-8/12 md:w-6/12 lg:w-5/12 p-6 rounded-xl shadow-lg space-y-4"
      >
        <h2 className="text-xl font-semibold text-gray-800">Add Student</h2>
        
        <input
          type="text"
          placeholder="Name"
          className="w-full p-2 border rounded"
          value={formData.name}
          onChange={(e) => setFormData({ ...formData, name: e.target.value })}
          required
        />
        <input
          type="email"
          placeholder="Email"
          className="w-full p-2 border rounded"
          value={formData.email}
          onChange={(e) => setFormData({ ...formData, email: e.target.value })}
          required
        />
        <input
          type="tel"
          placeholder="Phone"
          className="w-full p-2 border rounded"
          value={formData.phone}
          onChange={(e) => setFormData({ ...formData, phone: e.target.value })}
          required
        />
        <input
          type="text"
          placeholder="Codeforces Handle"
          className="w-full p-2 border rounded"
          value={formData.handle}
          onChange={(e) => setFormData({ ...formData, handle: e.target.value })}
          required
        />

        <div className="flex justify-end space-x-2 pt-2">
          <button
            type="button"
            onClick={handlecancel}
            className="px-4 py-2 bg-gray-300 rounded hover:bg-gray-400"
          >
            Cancel
          </button>
          <button
            type="submit"
            className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
          >
            Save
          </button>
        </div>
      </form>
    </div>
  );
}
