import React, { useState, useEffect } from 'react';

const student = {
  id: 1,
  name: 'Alice Johnson',
  email: 'alice@gmail.com',
    phone: '+1234567890',
    handle: 'alice_cf',
};

export default function EditStudentForm({ setOpenEditForm ,c }) {
  const [formData, setFormData] = useState({
    id : student.id,
    name: student.name,
    email: student.email,
    phone: student.phone,
    handle: student.handle,
  });

  useEffect(() => {
    if (student) {
      setFormData(student);
    }
  }, [student]);

  const handleSubmit = (e) => {
    e.preventDefault();
    // onSave({ ...formData });
  };

  const handlecancel = () => {
    setOpenEditForm(false);
  }

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center backdrop-blur-sm bg-black/30">
      <form
        onSubmit={handleSubmit}
        className="bg-white w-11/12 sm:w-8/12 md:w-6/12 lg:w-5/12 p-6 rounded-xl shadow-lg space-y-4"
      >
        <h2 className="text-xl font-semibold text-gray-800">Edit Student</h2>

        <input
          type="text"
          placeholder="Name"
          className="w-full p-2 border rounded"
          value={formData.name}
          onChange={(e) => setFormData({ ...formData, name: e.target.value })}
          required
        />
        <input
          type="email"
          placeholder="Email"
          className="w-full p-2 border rounded"
          value={formData.email}
          onChange={(e) => setFormData({ ...formData, email: e.target.value })}
          required
        />
        <input
          type="tel"
          placeholder="Phone"
          className="w-full p-2 border rounded"
          value={formData.phone}
          onChange={(e) => setFormData({ ...formData, phone: e.target.value })}
          required
        />
        <input
          type="text"
          placeholder="Codeforces Handle"
          className="w-full p-2 border rounded"
          value={formData.handle}
          onChange={(e) => setFormData({ ...formData, handle: e.target.value })}
          required
        />

        <div className="flex justify-end space-x-2 pt-2">
          <button
            type="button"
            onClick={handlecancel}
            className="px-4 py-2 bg-gray-300 rounded hover:bg-gray-400"
          >
            Cancel
          </button>
          <button
            type="submit"
            className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
          >
            Save
          </button>
        </div>
      </form>
    </div>
  );
}
