import React, { useState, useEffect } from 'react';

export default function EditStudentForm({ setOpenEditForm, student }) {
  const [formData, setFormData] = useState({
    id: student.id,
    name: student.name,
    email: student.email,
    phone: student.phone,
    handle: student.handle,
  });

  useEffect(() => {
    if (student) {
      setFormData(student);
    }
  }, [student]);

  const handleSubmit = (e) => {
    e.preventDefault();
    // onSave({ ...formData });
  };

  const handleCancel = () => {
    setOpenEditForm(false);
  };

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center backdrop-blur-sm bg-black/30">
      <form
        onSubmit={handleSubmit}
        className="w-11/12 sm:w-8/12 md:w-6/12 lg:w-5/12 p-6 rounded-xl shadow-lg 
                   bg-white dark:bg-zinc-950 space-y-4 transition-colors"
      >
        <h2 className="text-xl font-semibold text-gray-800 dark:text-white">Edit Student</h2>

        <input
          type="text"
          placeholder="Name"
          className="w-full p-2 border rounded bg-white dark:bg-zinc-900 text-gray-900 dark:text-white border-gray-300 dark:border-zinc-700"
          value={formData.name}
          onChange={(e) => setFormData({ ...formData, name: e.target.value })}
          required
        />
        <input
          type="email"
          placeholder="Email"
          className="w-full p-2 border rounded bg-white dark:bg-zinc-900 text-gray-900 dark:text-white border-gray-300 dark:border-zinc-700"
          value={formData.email}
          onChange={(e) => setFormData({ ...formData, email: e.target.value })}
          required
        />
        <input
          type="tel"
          placeholder="Phone"
          className="w-full p-2 border rounded bg-white dark:bg-zinc-900 text-gray-900 dark:text-white border-gray-300 dark:border-zinc-700"
          value={formData.phone}
          onChange={(e) => setFormData({ ...formData, phone: e.target.value })}
          required
        />
        <input
          type="text"
          placeholder="Codeforces Handle"
          className="w-full p-2 border rounded bg-white dark:bg-zinc-900 text-gray-900 dark:text-white border-gray-300 dark:border-zinc-700"
          value={formData.handle}
          onChange={(e) => setFormData({ ...formData, handle: e.target.value })}
          required
        />

        <div className="flex justify-end space-x-2 pt-2">
          <button
            type="button"
            onClick={handleCancel}
            className="px-4 py-2 bg-gray-300 rounded hover:bg-gray-400 text-gray-800 dark:bg-zinc-800 dark:hover:bg-zinc-700 dark:text-gray-200"
          >
            Cancel
          </button>
          <button
            type="submit"
            className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
          >
            Save
          </button>
        </div>
      </form>
    </div>
  );
}
